package com.studio10.edricchan.myfirstapp;import android.content.Intent;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.EditText;import android.widget.TextView;public class MainActivity extends AppCompatActivity {    public final static String EXTRA_MESSAGE = "com.studio10.edricchan.myfirstapp.MESSAGE";    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);    }    /** Called when the user clicks the Send button */    public void sendMessage(View view) {        Intent intent = new Intent(this, DisplayMessageActivity.class);        EditText editText = (EditText) findViewById(R.id.edit_message);        String message = editText.getText().toString();        intent.putExtra(EXTRA_MESSAGE, message);        startActivity(intent);    }    /** Called when the user clicks the destroy button */    public void destroyActivity() {        super.onDestroy();        android.os.Debug.stopMethodTracing();    }    /** Called when the user clicks on Preferences */    public void preferences() {        Intent preferences = new Intent(this, MyPreferenceActivity.class);        startActivity(preferences);    }}